import React, {useState} from 'react'


export default function TextForm(props) {
  const [text, setText] = useState(''); 
  // setText("new text");
  const handleUpClick = () => {
    console.log("Uppercase was clicked");
    let newText = text.toLocaleUpperCase();
    setText(newText)
}
 const handleToggleCaseClick = () => {
    let words = text.split(" ");
    let newText = words
      .map((word) => {
        let newWord = "";
        for (let i = 0; i < word.length; i++) {
          let char = word.charAt(i);
          if (char >= "A" && char <= "Z") {
            char = char.toLowerCase();
          } else if (char >= "a" && char <= "z") {
            char = char.toUpperCase();
          }
          newWord += char;
        }

        return newWord;
      })
      .join(" ");

    setText(newText);
  };

//this converts text to sentence case meaning the first word starts with a captial letter the rest of the words are lowercase
const handleSentenceCaseClick = () => {
    let lowerCase = text.toLowerCase();
    let regex = /([^.!?]+[!?.\d\s]+)/g;
    let sentences = lowerCase.match(regex);
    let newText = sentences
      .map((sentence) => {
        return (sentence.charAt(0) >= "a" && sentence.charAt(0) <= "z"
          ? sentence.charAt(0).toUpperCase() + sentence.slice(1)
          : sentence);
      })
      .join("");

    setText(newText);
  };

//this function converts the first letter of every word to uppercase the rest of letters are lower case
const handleCapitalizeWordClick = () => {
    let lowercase = text.toLowerCase();
    let words = lowercase.split(" ");
    let newWords = words.map((word) => {
      return word.charAt(0).toUpperCase() + word.slice(1);
    });
    let newText = newWords.join(" ");
    setText(newText);
  };
const handleDownClick = () => {
    console.log("lowercase was clicked");
    let newText = text.toLocaleLowerCase();
    setText(newText)
}
const handleOnChange = (event) => {
    console.log("On change");
    setText(event.target.value);
}

  return (
    <>
    <div>
      <h1>{props.heading}</h1>
      <div class="mb-3">
        <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
      </div>
      
      <button className="btn btn-primary mx-2" onClick={handleUpClick}>Covert to Uppercase</button>
      <button className="btn btn-primary my-3 mx-2" onClick={handleDownClick}>Covert to Lowercase</button>
    </div>
    <div className='container my-3'>
    <h2>Your Input Summary</h2>
    <p1>You have used {text.split(" ").length-1} words</p1>
    <p>You have used {text.length} letters</p>
    <p>it will take about {0.008 * text.split(" ").length} minutes to read</p>
  </div>
    </>

  )
}
